mapper.select:
select

,IF((select count(*) from sys_menu smu where smu.parent_id=sm.menu_id)>0,1,0) as isLeaf
from sys_menu sm


        and menu_id = #{menuId}


        and parent_id = #{parentId}


        and route_name = #{routeName}


        and menu_name = #{menuName}


        and menu_url = #{menuUrl}


        and status = #{status}
